import unittest

from src.adjective_core import convert_to_singular

class TestAdjectiveToSingular(unittest.TestCase):
    # test_args has the format [{
    #    "in":     ..., # (required)
    #    "out":    ..., # (required)
    #    "desc":   ..., # (optional) 
    #    "kwargs": ...  # (optional)
    # }, ...
    # ]
    test_args = [
        {'in': 'a', 'out': 'a'},
        {'in': 'my', 'out': 'my'},
        {'in': 'our', 'out': 'my'},
        {'in': 'some', 'out': 'a'},
        {'in': 'that', 'out': 'that'},
        {'in': 'their', 'out': 'their'},
        {'in': 'these', 'out': 'this'},
        {'in': 'this', 'out': 'this'},
        {'in': 'those', 'out': 'that'},
        {'in': 'your', 'out': 'your'},
    ]

    def test_convert_to_singular(self):
        for test_case in self.test_args:
            with self.subTest():
                # Expand test_case with default cases, if optional keys are not provided
                test_case = {**test_case, **{
                    "desc": f"convert_to_singular({repr(test_case['in'])}) => {repr(test_case['out'])}",
                    "kwargs": dict()
                }}
                self.assertEqual(convert_to_singular(test_case["in"], **test_case["kwargs"]), test_case["out"], test_case["desc"])

if __name__ == "__main__":
    unittest.main()
